$primary-color: #ffbf47;
$secondary-color: #ffcb6b;
$tertiary-color: #fde4c4;
$font-family: 'Arial', sans-serif;

// Variables pour les bordures des boutons
$border-width: 2px;
$border-radius: 50px;

body {
    margin: 0px;
    padding: 0px;
    font-family: sans-serif;
    -webkit-text-size-adjust: 100%;
    -ms-text-size-adjust: 100%;
}

#accueil.hidden, #nina.hidden, #jep.hidden, #défaite.hidden {
    display: none;
}

// Début du code pour la section accueil
.center {
    display: flex;
    justify-content: center;
    align-items: center;
  }
  
  .accueil {
    height: 100vh;
    display: flex;
    flex-direction: column;
    background-image: url('./img/écran accueil.png');
    background-repeat: no-repeat;
    background-size: cover;
    
    .form {
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      background-color: brown;
      border-radius: 50px;
      padding: 20px;
    }
    
    .form input[type="text"] {
      width: 100%;
      padding: 10px;
      border-radius: 50px;
      border: none;
      font-size: 18px;
    }
    
    .character {
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      margin-top: 400px;
      
      .image-wrapper {
        display: flex;
        justify-content: center;
        align-items: center;
        margin: 10px;
        
        button {
          border: none;
          background-color: transparent;
          margin: 10px;
          border-radius: 50%;

          &:hover {
            border: 3px solid $primary-color;
          }
          
          &:focus {
            outline: none;
            border: 3px solid $primary-color;
        }

          &:active {
            border: 3px solid $primary-color;
        }
          
          img {
            width: 150px;
            height: 150px;
            border-radius: 50%;
            transition: all 0.2s ease-in-out;

            &:hover {
              transform: scale(1.05);
            }

            &:focus {
              transform: scale(1.05);
            }

            &:active {
              transform: scale(1.05);
            }
          }
        }
      }
    }
    
    .action {
      display: flex;
      justify-content: center;
      margin-top: 20px;
    }
    
    .bouton {
      display: inline-block;
      background-color: rgb(23, 201, 23);
      color: black;
      font-size: 1.5rem;
      font-weight: bold;
      padding: 1rem 2rem;
      padding-left: 15px;
      padding-right: 15px;
      text-align: center;
      text-decoration: none;
      border-radius: $border-radius;
      cursor: pointer;
      transition: all 0.2s ease-in-out;
    }
    
    .bouton:hover {
      transform: scale(1.05);
    }
  }

  .nina {
    height: 100vh;
    display: flex;
    background-image: url('./img/nautilus.png');
    background-repeat: no-repeat;
    background-size: cover;

    .container {
      display: flex;
      align-items: center; 
      justify-content: center; /* Espacement entre les deux groupes d'éléments */
    }

    .picture {
      padding: 100px;
    }

    .picture img {
      width: 150px;
      height: 150px;
      border-radius: 50%;
    }

    .nina-img {
      animation-name: tilt-shaking;
      animation-duration: 0.5s;
      animation-timing-function: ease-in-out;
      animation-iteration-count: infinite;
    }

    @keyframes tilt-shaking {
      0% { transform: rotate(0deg); }
      25% { transform: rotate(5deg); }
      50% { transform: rotate(0eg); }
      75% { transform: rotate(-5deg); }
      100% { transform: rotate(0deg); }
    }

    .icons {
      display: flex;
      flex-direction: column;
      align-items: center;
      margin-top: 20px;
    }
    
    .icons img {
      width: 80px;
      height: 80px;
      margin-bottom: 10px;
      cursor: pointer;
    }
    
    .color_bar {
      display: flex;
      flex-direction: column;
      align-items: center;
      margin-top: 50px;
    }
    
    .color_bar span {
      display: flex;
      flex-direction: column;
      width: 400px;
      height: 50px;
      margin-right: 5px;
      border-radius: 50px;
      margin-bottom: 35px;
      margin-top: 11px;
      border: 5px solid black;
    }
    
    .color_bar .red {
      background-color: red; /* Barre de couleur rouge */
    }
    
    .color_bar .orange {
      background-color: orange; /* Barre de couleur orange */
    }
    
    .color_bar .green {
      background-color: green; /* Barre de couleur verte */
    }
}

.jep {
  height: 100vh;
  display: flex;
  background-image: url('./img/nautilus.png');
  background-repeat: no-repeat;
  background-size: cover;

  .container {
    display: flex;
    align-items: center; 
    justify-content: center; /* Espacement entre les deux groupes d'éléments */
  }

  .picture {
    padding: 100px;
  }

  .picture img {
    width: 150px;
    height: 150px;
    border-radius: 50%;
  }

  .jep-img {
    animation-name: tilt-shaking;
    animation-duration: 0.5s;
    animation-timing-function: ease-in-out;
    animation-iteration-count: infinite;
  }

  @keyframes tilt-shaking {
    0% { transform: rotate(0deg); }
    25% { transform: rotate(5deg); }
    50% { transform: rotate(0eg); }
    75% { transform: rotate(-5deg); }
    100% { transform: rotate(0deg); }
  }

    .icons {
      display: flex;
      flex-direction: column;
      align-items: center;
      margin-top: 20px;
    }
  
    .icons img {
      width: 80px;
      height: 80px;
      margin-bottom: 10px;
      cursor: pointer;
    }
    
    .color_bar {
      display: flex;
      flex-direction: column;
      align-items: center;
      margin-top: 50px;
    }
    
    .color_bar span {
      display: flex;
      flex-direction: column;
      width: 400px;
      height: 50px;
      margin-right: 5px;
      border-radius: 50px;
      margin-bottom: 35px;
      margin-top: 11px;
      border: 5px solid black;
    }
    
    .color_bar .red {
      background-color: red; /* Barre de couleur rouge */
    }
    
    .color_bar .orange {
      background-color: orange; /* Barre de couleur orange */
    }
    
    .color_bar .blue {
      background-color:deepskyblue; /* Barre de couleur verte */
    }

}

.defeat {
  height: 100vh;
  background-image: url('./img/défaite.png');
  background-repeat: no-repeat;
  background-size: cover;

  .container {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    height: 100%;
  }

  .text {
    padding: 30px;
    max-width: 500px;
    border-radius: 5px;
    margin-left: auto;
    margin-right: auto;
  }

  .text p {
    padding: 50px;
    font-size: 25px;
    color: black;
    background-color: darkgrey;
  }

  .new_game button {
    display: inline-block;
    background-color: rgb(23, 201, 23);
    color: black;
    font-size: 1.5rem;
    font-weight: bold;
    padding: 1rem 2rem;
    padding-left: 15px;
    padding-right: 15px;
    text-decoration: none;
    border-radius: $border-radius;
    cursor: pointer;
    transition: all 0.2s ease-in-out;
  }
  
  .new_game button:hover {
    transform: scale(1.05);
  }

}

@media screen and (max-width: 640px) {
  .accueil {
    background-position: center;
    height: 100%;

  .container {
      margin-bottom: 10px;
    }
  }

  .nina {
    height: 100%;
    background-position: center;
    
  .container {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

  .icons {
        display: flex;
        align-items: center;
      }
    }
  }

  .jep {
    height: 100%;
    background-position: center;
    
  .container {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

  .icons {
    display: flex;
    align-items: center;
    }
  }
}

  .défaite {
    background-position: center;

  .container {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    }
  }
}
